INSERT INTO product VALUES ('100','33','/uploads/images/Code%20Scanner/FR704%20car%20code%20reader.jpg','FR704 car code reader','<p>\r\n	<font style=\"font-size: 12px; font-family: Arial;\">FR704 car code reader</font></p>\r\n<p>\r\n	<font style=\"font-size: 12px; font-family: Arial;\">Reads Engine, Transmission, ABS &amp; Airbag Systems for major Japanese vehicles!</font></p>\r\n<p>\r\n	<font style=\"font-size: 12px; font-family: Arial;\">Powered by Autel Uni-SCAN technology, the MaxiDiag FR704 is an innovative multi-functional scan tool designed for </font></p>\r\n<p>\r\n	<font style=\"font-size: 12px; font-family: Arial;\">technicians to troubleshoot Engine, Transmission, ABS &amp; Airbag systems for Renault/Citroen/Peugeot. Reliable, easy-to-use </font></p>\r\n<p>\r\n	<font style=\"font-size: 12px; font-family: Arial;\">and affordable, the MaxiDiag FR704 is a time saver and a money maker! </font></p>\r\n<p>\r\n	<font style=\"font-size: 12px; font-family: Arial;\">Features</font></p>\r\n<p>\r\n	<font style=\"font-size: 12px; font-family: Arial;\">&raquo;Troubleshoots engine, ABS, transmission &amp; airbag codes.<br style=\"\" />\r\n	&raquo;Global OBD2/EOBD coverage (US, Asian &amp; European).<br style=\"\" />\r\n	&raquo;Powered by Uni-SCAN technology with no adapters to buy.<br style=\"\" />\r\n	&raquo;Supports all nine test modes of the latest J1979 OBD2 test specs.<br style=\"\" />\r\n	&raquo;Retrieves generic (P0, P2, P3, and U0) &amp; manufacturer specific (P1, P3, and U1) codes.<br style=\"\" />\r\n	&raquo;Displays DTC definitions onscreen.<br style=\"\" />\r\n	&raquo;Views freeze frame data.<br style=\"\" />\r\n	&raquo;Displays O2 sensor data as well as live datastream parameters.<br style=\"\" />\r\n	&raquo;Data graphing (1996 and newer).<br style=\"\" />\r\n	&raquo;Reads, stores &amp; playbacks live sensor data.<br style=\"\" />\r\n	&raquo;Memory/battery backup for off-vehicle data review &amp; analysis.<br style=\"\" />\r\n	&raquo;Performs modules present test.<br style=\"\" />\r\n	&raquo;Retrieves vehicle information (VIN, CID &amp; CVN).<br style=\"\" />\r\n	&raquo;Supports all current OBD2 protocols, including CAN (Controller Area Network).<br style=\"\" />\r\n	&raquo;Extremely easy to use &amp; highly reliable.<br style=\"\" />\r\n	&raquo;Trilingual supportEnglish, Spanish, French or other combinations.<br style=\"\" />\r\n	&raquo;Software upgradeable via internet.<br style=\"\" />\r\n	&raquo;Built-in OBD2 DTC lookup library.<br style=\"\" />\r\n	&raquo;Prints data via PC.</font></p>\r\n<p>\r\n	<br />\r\n	<font style=\"font-size: 12px; font-family: Arial;\">Vehicle coverage </font></p>\r\n<p>\r\n	<font style=\"font-size: 12px; font-family: Arial;\">&raquo;Global OBD II compliant US, European &amp; Asian vehicles.<br style=\"\" />\r\n	&raquo;Renault 1996-2007 <br style=\"\" />\r\n	&raquo;Peugeot 1996-2007 <br style=\"\" />\r\n	&raquo;Citroen 1996-2005</font></p>\r\n<p>\r\n	<br />\r\n	<font style=\"font-size: 12px; font-family: Arial;\">Includes</font></p>\r\n<p>\r\n	<font style=\"font-size: 12px; font-family: Arial;\">&raquo;User&#39;s Manual <br style=\"\" />\r\n	&raquo;OBD2 cable <br style=\"\" />\r\n	&raquo;USB cable <br style=\"\" />\r\n	&raquo;1.5V battery X 4<br style=\"\" />\r\n	&raquo;Nylon carry case </font></p>\r\n','1257952355','1257956660','0','0','1','0','','1','1','no');
INSERT INTO product VALUES ('101','33','/uploads/images/Code%20Scanner/VAG405.jpg','VAG405 Code reader VW/AUDI OBD2 EOBD CAN BUS','<p>\r\n	<font style=\"font-family: Arial;\">The MaxScan VAG405 code scanner is an OEM level diagnostic scan tool for most VW/AUDIs sold worldwide since 1990 to present INC CAN BUS. Accurate, reliable and <br style=\"\" />\r\n	<br style=\"\" />\r\n	easy to use, It can access MOST modules, the MaxScan VAG405 is a time saver and a money maker!<br style=\"\" />\r\n	<br style=\"\" />\r\n	It also doubles up as a Generic OBD2 fault code reader for most other manufacturers.. (Engine DTC Check)<br style=\"\" />\r\n	<br style=\"\" />\r\n	Features &amp; Benefits<br style=\"\" />\r\n	1. Works with Volkswagen and Audi(Audi/Seat/Skoda/Jetta/Golf/Beetle/Touareg/GTI/ Passat and more) sold worldwide since 1990<br style=\"\" />\r\n	<br style=\"\" />\r\n	2. Reads and erases Diagnostic Trouble Codes (DTCs) of most electronically controlled vehicle modules, including Engine, ABS, Airbags, Transmission, Radio, Climate <br style=\"\" />\r\n	<br style=\"\" />\r\n	Control(A/C), Cent.Elect. Steering Wheel, Instruments, Immobilizer, etc.<br style=\"\" />\r\n	<br style=\"\" />\r\n	3. Reads and clears generic trouble codes of cars using ISO 9141-2 (&quot;CARB&quot;), ISO 14230 (&quot;KWP-2000&quot;) and ISO-15765 (&quot;CAN&quot;) protocols. <br style=\"\" />\r\n	<br style=\"\" />\r\n	4. Turns off MIL lights of engine, airbags, ABS , A/T and most other systems<br style=\"\" />\r\n	<br style=\"\" />\r\n	5. C.A.N.(Controller Area Network) Capability<br style=\"\" />\r\n	<br style=\"\" />\r\n	6. Resets oil service light<br style=\"\" />\r\n	<br style=\"\" />\r\n	7. Retrieves vehicle information (VIN)<br style=\"\" />\r\n	<br style=\"\" />\r\n	8. Displays I/M Readiness Status.<br style=\"\" />\r\n	<br style=\"\" />\r\n	9. Displays DTC definitions on Unit Screen<br style=\"\" />\r\n	<br style=\"\" />\r\n	10. Highly reliable and accurate<br style=\"\" />\r\n	<br style=\"\" />\r\n	11. Easy to use with friendly interface<br style=\"\" />\r\n	<br style=\"\" />\r\n	12. Safely communicates with the on-board computers<br style=\"\" />\r\n	<br style=\"\" />\r\n	13. Large, easy-to-read backlit LCD<br style=\"\" />\r\n	<br style=\"\" />\r\n	14. System Upgradeable via internet<br style=\"\" />\r\n	<br style=\"\" />\r\n	15. Stand-alone unit with no need for an additional laptop computer to operate<br style=\"\" />\r\n	This Tool performs as a FULL diagnostic tool for VAG range and as a Generic Fault Code Reader for other Vehicles that are OBD2 complient..<br style=\"\" />\r\n	<br style=\"\" />\r\n	Package Includes:<br style=\"\" />\r\n	<br style=\"\" />\r\n	MaxScan VAG 405 HandHeld Scanner<br style=\"\" />\r\n	Carry/Storage Case<br style=\"\" />\r\n	16 Pin OBD 2 Data Link cable<br style=\"\" />\r\n	USB cable for updates<br style=\"\" />\r\n	User Manual</font></p>\r\n','1257952532','0','0','0','1','0','','1','1','no');
